mapper.select:
SELECT ROWNUM AS listSeq , T."QI_ID" as qiID , T.QI_WORK_ID as workID , T.QI_PRO_LOT_ID as lotID , T.PI_MACHINE_ID as machineID , T.QI_PI_ID as processID , T.PI_NAME as processName , T.CT_UNIT as unit , T.W_ITEM_QUANTITY as dailyWorkQuantity , T.QI_INSPECTION_QUANTITY as inspectionQuantity , T.QI_DFT_QUANTITY as defectQuantity , T.defectRate as defectRate , T.QI_PASSED_QUANTITY as goodQuantity , T.QS_DF_DIV1 as qsDiv1 , T.QS_DF_DIV2 as qsDiv2 , T.inspectionDate as inspectionDate , T.QI_NOTE as note FROM ( SELECT DISTINCT qi.QI_ID as "QI_ID" , qi.QI_WORK_ID , qi.QI_PRO_LOT_ID , pi2.PI_MACHINE_ID , qi.QI_PI_ID , pi2.PI_NAME , w.W_ITEM_QUANTITY , qi.QI_INSPECTION_QUANTITY , qi.QI_DFT_QUANTITY , (qi.QI_DFT_QUANTITY / NULLIF(qi.QI_INSPECTION_QUANTITY, 0) * 100) AS defectRate , qi.QI_PASSED_QUANTITY , qs.QS_DF_DIV1 , qs.QS_DF_DIV2 , TO_CHAR(qi.QI_DATE, 'YYYY-MM-DD') AS inspectionDate , qi.QI_NOTE , ct.CT_UNIT FROM QUALITY_INSPECTION qi JOIN PROCESS_INFO pi2 ON pi2.PI_ID = qi.QI_PI_ID JOIN WORK w ON w.W_ID = qi.QI_WORK_ID JOIN QUALITY_STANDARD qs ON qs.QS_ID = qi.QI_QS_ID JOIN INSTRUCTION ins ON ins.INS_LOT_ID = w.W_LOT_ID JOIN CONTRACT ct ON ct.CT_ID = ins.INS_CT_ID ORDER BY qi.QI_ID DESC ) T
and PRO_LOT_ID = #{lotID}
and PI_ID = #{processID}
and PI_NAME = #{processName}
and DF_DIV1 = #{qsDiv1}
and DF_DIV2 = #{qsDiv2}
and WORK_ID = #{workID}
and MI_ID = #{machineID}

mapper.insert:
INSERT INTO QUALITY_INSPECTION ( QI_PRO_LOT_ID, QI_PI_ID, QI_WORK_ID, QI_QS_ID, QI_INSPECTION_QUANTITY, QI_PASSED_QUANTITY, QI_DFT_QUANTITY, QI_DATE, QI_NOTE ) VALUES ( #{lotID}, #{processID}, #{workID},
( SELECT qs.QS_ID FROM QUALITY_STANDARD qs WHERE qs.QS_DF_DIV1 = #{qsDiv1}
AND qs.QS_DF_DIV2 = #{qsDiv2}
) , #{inspectionQuantity}, #{goodQuantity}, #{defectQuantity}, #{inspectionDate}
, #{note}
)

mapper.update:
UPDATE
    "WORK"
SET
    W_DFT_QUANTITY
    = (
    #{inspectionQuantity} - #{goodQuantity}
    )
WHERE
    W_ID = #{workID}
    AND W_DFT_QUANTITY IS NULL

UPDATE
    QUALITY_INSPECTION
SET
QI_QS_ID = (
            SELECT qs.QS_ID
            FROM QUALITY_STANDARD qs
            WHERE qs.QS_DF_DIV1 = #{qsDiv1}

                AND qs.QS_DF_DIV2 = #{qsDiv2}

            )

    , QI_NOTE = #{note}

WHERE
    QI_ID = #{qiID}
